
/**
`PlatformTextureMedia` is a base class for different types of media that can be used as platform textures.
*/
declare interface PlatformTextureMediaBase {

/**
```
(get) mediaId: string
(set) (Not Available)
```

Returns an unique identifer of the media.
*/
mediaId: string

/**
```
(get) mediaType: PlatformTextureMediaType
(set) (Not Available)
```

Returns the type of media being used.
*/
mediaType: PlatformTextureMediaType

}
