/**
The `BuiltinUniform` enum describes the bultin shader uniforms.
*/
declare enum BuiltinUniform {

/**
Inverse Model Matrix uniform.
*/
INV_M_MATRIX,

/**
Inverse Projection Matrix uniform.
*/
INV_P_MATRIX,

/**
Inverse View Matrix uniform.
*/
INV_V_MATRIX,

/**
Model View Projection Matrix uniform.
*/
MVP_MATRIX,

/**
Model View Matrix uniform.
*/
MV_MATRIX,

/**
Model Matrix uniform.
*/
M_MATRIX,

/**
Normal Matrix uniform.
*/
NORMAL_MATRIX,

/**
Projection Matrix uniform.
*/
P_MATRIX,

/**
View Projection Matrix uniform.
*/
VP_MATRIX,

/**
View Matrix uniform.
*/
V_MATRIX,

}
