/**
The `VertexAttribute` enum describes the bultin vertex attributes.
*/
declare enum VertexAttribute {

/**
Base Position vertex attribute.
*/
BASE_POSITION,

/**
Color vertex attribute.
*/
COLOR,

/**
Normal vertex attribute.
*/
NORMAL,

/**
Position vertex attribute.
*/
POSITION,

/**
Tangent vertex attribute.
*/
TANGENT,

/**
Texture Coordinates vertex attribute.
*/
TEX_COORDS,

}
